import { useState, useEffect } from 'react';
import { ThemeProvider, CssBaseline, Box } from '@mui/material';
import { keyframes } from '@mui/system';
import { lightTheme, darkTheme } from './theme';
import TextAnalyzer from './components/TextAnalyzer';
import ThemeToggle from './components/ThemeToggle';

const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const glowAnimation = keyframes`
  0% { opacity: 0.3; }
  50% { opacity: 0.5; }
  100% { opacity: 0.3; }
`;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 2 - 1,
        y: (e.clientY / window.innerHeight) * 2 - 1,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: '100vh',
          position: 'relative',
          overflow: 'hidden',
          background: theme => 
            theme.palette.mode === 'light'
              ? 'linear-gradient(135deg, #F0F7FF, #FFFFFF)'
              : 'linear-gradient(135deg, #0A1929, #1A1A2B)',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: theme =>
              theme.palette.mode === 'light'
                ? `radial-gradient(circle at ${50 + mousePosition.x * 10}% ${50 + mousePosition.y * 10}%, 
                    rgba(124, 207, 255, 0.1) 0%,
                    rgba(77, 208, 225, 0.1) 25%,
                    rgba(54, 191, 250, 0.1) 50%,
                    rgba(47, 212, 182, 0.1) 75%,
                    rgba(255, 255, 255, 0) 100%)`
                : `radial-gradient(circle at ${50 + mousePosition.x * 10}% ${50 + mousePosition.y * 10}%, 
                    rgba(54, 191, 250, 0.15) 0%,
                    rgba(47, 212, 182, 0.15) 25%,
                    rgba(77, 208, 225, 0.1) 50%,
                    rgba(124, 207, 255, 0.05) 75%,
                    rgba(0, 0, 0, 0) 100%)`,
            animation: `${glowAnimation} 4s ease-in-out infinite`,
            pointerEvents: 'none',
          },
          '&::after': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(45deg, rgba(255,255,255,0.5) 0%, rgba(255,255,255,0) 100%)'
                : 'linear-gradient(45deg, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0) 100%)',
            backgroundSize: '200% 200%',
            animation: `${gradientAnimation} 15s ease infinite`,
            pointerEvents: 'none',
          },
          // 添加网格效果
          '& .grid': {
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: theme =>
              theme.palette.mode === 'light'
                ? `linear-gradient(90deg, rgba(124, 207, 255, 0.1) 1px, transparent 1px),
                   linear-gradient(0deg, rgba(124, 207, 255, 0.1) 1px, transparent 1px)`
                : `linear-gradient(90deg, rgba(54, 191, 250, 0.05) 1px, transparent 1px),
                   linear-gradient(0deg, rgba(54, 191, 250, 0.05) 1px, transparent 1px)`,
            backgroundSize: '50px 50px',
            opacity: theme => theme.palette.mode === 'light' ? 0.5 : 1,
            pointerEvents: 'none',
          },
          padding: { xs: 2, md: 4 },
        }}
      >
        <div className="grid" />
        <Box
          sx={{
            position: 'relative',
            zIndex: 1,
            '& .MuiCard-root': {
              background: theme =>
                theme.palette.mode === 'light'
                  ? 'rgba(255, 255, 255, 0.8)'
                  : 'rgba(17, 25, 40, 0.75)',
              backdropFilter: 'blur(20px)',
              border: theme =>
                theme.palette.mode === 'light'
                  ? '1px solid rgba(255, 255, 255, 0.3)'
                  : '1px solid rgba(255, 255, 255, 0.125)',
            },
          }}
        >
          <ThemeToggle isDarkMode={isDarkMode} toggleTheme={() => setIsDarkMode(!isDarkMode)} />
          <TextAnalyzer />
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
