import { useState, useEffect } from 'react';
import { Card, Typography, Box, Fade } from '@mui/material';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAlt';
import SentimentNeutralIcon from '@mui/icons-material/SentimentNeutral';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';

const EmotionChart = ({ history }) => {
  const [hoveredId, setHoveredId] = useState(null);
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);
  }, [history]);

  const sentimentCounts = history.reduce((acc, item) => {
    acc[item.sentiment] = (acc[item.sentiment] || 0) + 1;
    return acc;
  }, {});

  const total = history.length;
  const getPercentage = (value) => ((value || 0) / total * 100).toFixed(1);

  const data = [
    {
      id: 'positive',
      label: '正面',
      value: sentimentCounts.positive || 0,
      gradient: ['#00ff87', '#60efff'],
      icon: <SentimentSatisfiedAltIcon />,
    },
    {
      id: 'neutral',
      label: '中性',
      value: sentimentCounts.neutral || 0,
      gradient: ['#0061ff', '#60efff'],
      icon: <SentimentNeutralIcon />,
    },
    {
      id: 'negative',
      label: '负面',
      value: sentimentCounts.negative || 0,
      gradient: ['#ff0f7b', '#f89b29'],
      icon: <SentimentVeryDissatisfiedIcon />,
    },
  ];

  if (history.length === 0) {
    return (
      <Card
        sx={{
          p: 3,
          height: '100%',
          minHeight: 400,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          gap: 2,
          background: 'rgba(0,0,0,0.2)',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255,255,255,0.1)',
          boxShadow: '0 4px 30px rgba(0,0,0,0.1)',
        }}
      >
        <Box
          sx={{
            width: '150px',
            height: '150px',
            position: 'relative',
            '&::before': {
              content: '""',
              position: 'absolute',
              inset: 0,
              background: 'conic-gradient(from 0deg, #0061ff, #00ff87, #ff0f7b, #0061ff)',
              borderRadius: '50%',
              animation: 'rotate 4s linear infinite',
              filter: 'blur(10px)',
              opacity: 0.3,
            },
            '&::after': {
              content: '""',
              position: 'absolute',
              inset: 6,
              background: theme => theme.palette.background.paper,
              borderRadius: '50%',
              zIndex: 1,
            },
            '@keyframes rotate': {
              '0%': {
                transform: 'rotate(0deg)',
              },
              '100%': {
                transform: 'rotate(360deg)',
              },
            },
          }}
        >
          <Box
            sx={{
              position: 'absolute',
              inset: 0,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 2,
            }}
          >
            <SentimentNeutralIcon sx={{ fontSize: 48, opacity: 0.5 }} />
          </Box>
        </Box>
        <Typography
          sx={{
            background: 'linear-gradient(45deg, #0061ff, #00ff87)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 500,
          }}
        >
          暂无数据
        </Typography>
      </Card>
    );
  }

  return (
    <Card
      sx={{
        p: 3,
        minHeight: 400,
        background: 'rgba(0,0,0,0.2)',
        backdropFilter: 'blur(20px)',
        border: '1px solid rgba(255,255,255,0.1)',
        boxShadow: '0 4px 30px rgba(0,0,0,0.1)',
      }}
    >
      <Typography
        variant="h6"
        sx={{
          mb: 4,
          textAlign: 'center',
          background: 'linear-gradient(45deg, #0061ff, #00ff87)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          fontWeight: 600,
        }}
      >
        情感分布
      </Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4, position: 'relative' }}>
        <Box
          sx={{
            width: 260,
            height: 260,
            position: 'relative',
            transform: 'rotate(-90deg)',
          }}
        >
          <Box
            sx={{
              position: 'absolute',
              inset: -20,
              borderRadius: '50%',
              background: 'conic-gradient(from 0deg, #0061ff, #00ff87, #ff0f7b, #0061ff)',
              animation: 'rotate 8s linear infinite',
              filter: 'blur(20px)',
              opacity: 0.15,
              '@keyframes rotate': {
                '0%': { transform: 'rotate(0deg)' },
                '100%': { transform: 'rotate(360deg)' },
              },
            }}
          />
          {data.map((item, index) => {
            const percentage = (item.value / total) * 100;
            const offset = data
              .slice(0, index)
              .reduce((acc, curr) => acc + (curr.value / total) * 100, 0);

            return (
              <Box
                key={item.id}
                component="svg"
                sx={{
                  position: 'absolute',
                  width: '100%',
                  height: '100%',
                }}
              >
                <circle
                  cx="130"
                  cy="130"
                  r="100"
                  fill="none"
                  stroke={`url(#gradient-${item.id})`}
                  strokeWidth="24"
                  strokeDasharray={`${animate ? percentage : 0} ${100 - percentage}`}
                  strokeDashoffset={-offset}
                  style={{
                    transition: 'all 1s cubic-bezier(0.4, 0, 0.2, 1)',
                    transform: hoveredId === item.id ? 'scale(1.02)' : 'scale(1)',
                    transformOrigin: 'center',
                    filter: hoveredId === item.id ? 'drop-shadow(0 0 10px rgba(0,0,0,0.3))' : 'none',
                  }}
                />
                <defs>
                  <linearGradient id={`gradient-${item.id}`} x1="0%" y1="0%" x2="100%" y2="0%">
                    <stop offset="0%" stopColor={item.gradient[0]} />
                    <stop offset="100%" stopColor={item.gradient[1]} />
                  </linearGradient>
                </defs>
              </Box>
            );
          })}
        </Box>
      </Box>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          gap: 3,
        }}
      >
        {data.map((item) => (
          <Fade key={item.id} in timeout={500}>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1.5,
                padding: '10px 20px',
                borderRadius: '16px',
                transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                background: hoveredId === item.id
                  ? `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`
                  : 'rgba(255,255,255,0.03)',
                border: '1px solid',
                borderColor: hoveredId === item.id
                  ? 'rgba(255,255,255,0.2)'
                  : 'rgba(255,255,255,0.05)',
                color: hoveredId === item.id ? '#fff' : 'text.primary',
                cursor: 'pointer',
                backdropFilter: 'blur(10px)',
                '&:hover': {
                  background: `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`,
                  color: '#fff',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 20px rgba(0,0,0,0.2)',
                },
              }}
              onMouseEnter={() => setHoveredId(item.id)}
              onMouseLeave={() => setHoveredId(null)}
            >
              {item.icon}
              <Typography
                sx={{
                  fontWeight: 500,
                  minWidth: 100,
                  textAlign: 'center',
                  fontSize: '0.95rem',
                }}
              >
                {item.label}: {getPercentage(item.value)}%
              </Typography>
            </Box>
          </Fade>
        ))}
      </Box>
    </Card>
  );
};

export default EmotionChart;
