import { useState } from 'react';
import { Card, Typography, Box, Fade } from '@mui/material';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAlt';
import SentimentNeutralIcon from '@mui/icons-material/SentimentNeutral';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';

const EmotionChart = ({ history }) => {
  const [hoveredId, setHoveredId] = useState(null);

  const sentimentCounts = history.reduce((acc, item) => {
    acc[item.sentiment] = (acc[item.sentiment] || 0) + 1;
    return acc;
  }, {});

  const total = history.length;
  const getPercentage = (value) => ((value || 0) / total * 100).toFixed(1);

  const data = [
    {
      id: 'positive',
      label: '正面',
      value: sentimentCounts.positive || 0,
      color: '#4CAF50',
      gradient: ['#4CAF50', '#81C784'],
      icon: <SentimentSatisfiedAltIcon />,
    },
    {
      id: 'neutral',
      label: '中性',
      value: sentimentCounts.neutral || 0,
      color: '#2196F3',
      gradient: ['#2196F3', '#64B5F6'],
      icon: <SentimentNeutralIcon />,
    },
    {
      id: 'negative',
      label: '负面',
      value: sentimentCounts.negative || 0,
      color: '#F44336',
      gradient: ['#F44336', '#E57373'],
      icon: <SentimentVeryDissatisfiedIcon />,
    },
  ];

  if (history.length === 0) {
    return (
      <Card
        sx={{
          p: 3,
          height: '100%',
          minHeight: 400,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          gap: 2,
          position: 'relative',
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: '4px',
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(90deg, #e3f2fd, #bbdefb, #90caf9)'
                : 'linear-gradient(90deg, #424242, #616161, #424242)',
          },
        }}
      >
        <Box
          sx={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(135deg, #f5f5f5, #e0e0e0)'
                : 'linear-gradient(135deg, #424242, #212121)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: 0.5,
          }}
        >
          <SentimentNeutralIcon sx={{ fontSize: 48 }} />
        </Box>
        <Typography color="text.secondary">暂无数据</Typography>
      </Card>
    );
  }

  return (
    <Card
      sx={{
        position: 'relative',
        overflow: 'hidden',
        p: 3,
        minHeight: 400,
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '4px',
          background: theme =>
            theme.palette.mode === 'light'
              ? 'linear-gradient(90deg, #e3f2fd, #bbdefb, #90caf9)'
              : 'linear-gradient(90deg, #424242, #616161, #424242)',
        },
      }}
    >
      <Typography variant="h6" sx={{ mb: 3 }}>情感分布</Typography>
      <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
        <Box
          sx={{
            width: 200,
            height: 200,
            position: 'relative',
            transform: 'rotate(-90deg)',
          }}
        >
          {data.map((item, index) => {
            const percentage = (item.value / total) * 100;
            const offset = data
              .slice(0, index)
              .reduce((acc, curr) => acc + (curr.value / total) * 100, 0);

            return (
              <Box
                key={item.id}
                component="svg"
                sx={{
                  position: 'absolute',
                  width: '100%',
                  height: '100%',
                }}
              >
                <circle
                  cx="100"
                  cy="100"
                  r="80"
                  fill="none"
                  stroke={`url(#gradient-${item.id})`}
                  strokeWidth="20"
                  strokeDasharray={`${percentage} ${100 - percentage}`}
                  strokeDashoffset={-offset}
                  style={{
                    transition: 'all 0.3s ease-in-out',
                    transform: hoveredId === item.id ? 'scale(1.05)' : 'scale(1)',
                    transformOrigin: 'center',
                  }}
                />
                <defs>
                  <linearGradient id={`gradient-${item.id}`} x1="0%" y1="0%" x2="100%" y2="0%">
                    <stop offset="0%" stopColor={item.gradient[0]} />
                    <stop offset="100%" stopColor={item.gradient[1]} />
                  </linearGradient>
                </defs>
              </Box>
            );
          })}
        </Box>
      </Box>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          gap: 2,
        }}
      >
        {data.map((item) => (
          <Fade key={item.id} in timeout={500}>
            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                padding: '8px 16px',
                borderRadius: '12px',
                transition: 'all 0.3s ease',
                background: hoveredId === item.id
                  ? `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`
                  : 'transparent',
                color: hoveredId === item.id ? '#fff' : 'text.primary',
                cursor: 'pointer',
                '&:hover': {
                  background: `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`,
                  color: '#fff',
                },
              }}
              onMouseEnter={() => setHoveredId(item.id)}
              onMouseLeave={() => setHoveredId(null)}
            >
              {item.icon}
              <Typography
                variant="body2"
                sx={{
                  fontWeight: 500,
                  minWidth: 100,
                  textAlign: 'center',
                }}
              >
                {item.label}: {getPercentage(item.value)}%
              </Typography>
            </Box>
          </Fade>
        ))}
      </Box>
    </Card>
  );
};

export default EmotionChart;
