import { useState } from 'react';
import { Card, Typography, Box, Fade } from '@mui/material';
import { ResponsivePie } from '@nivo/pie';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAlt';
import SentimentNeutralIcon from '@mui/icons-material/SentimentNeutral';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';

const EmotionChart = ({ history }) => {
  const [hoveredId, setHoveredId] = useState(null);

  const sentimentCounts = history.reduce((acc, item) => {
    acc[item.sentiment] = (acc[item.sentiment] || 0) + 1;
    return acc;
  }, {});

  const total = history.length;
  const getPercentage = (value) => ((value || 0) / total * 100).toFixed(1);

  const data = [
    {
      id: 'positive',
      label: '正面',
      value: sentimentCounts.positive || 0,
      color: '#4CAF50',
      gradient: ['#4CAF50', '#81C784'],
      icon: <SentimentSatisfiedAltIcon />,
    },
    {
      id: 'neutral',
      label: '中性',
      value: sentimentCounts.neutral || 0,
      color: '#2196F3',
      gradient: ['#2196F3', '#64B5F6'],
      icon: <SentimentNeutralIcon />,
    },
    {
      id: 'negative',
      label: '负面',
      value: sentimentCounts.negative || 0,
      color: '#F44336',
      gradient: ['#F44336', '#E57373'],
      icon: <SentimentVeryDissatisfiedIcon />,
    },
  ];

  if (history.length === 0) {
    return (
      <Card
        sx={{
          p: 3,
          height: '100%',
          minHeight: 400,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          gap: 2,
          position: 'relative',
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            height: '4px',
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(90deg, #e3f2fd, #bbdefb, #90caf9)'
                : 'linear-gradient(90deg, #424242, #616161, #424242)',
          },
        }}
      >
        <Box
          sx={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(135deg, #f5f5f5, #e0e0e0)'
                : 'linear-gradient(135deg, #424242, #212121)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: 0.5,
          }}
        >
          <SentimentNeutralIcon sx={{ fontSize: 48 }} />
        </Box>
        <Typography color="text.secondary">暂无数据</Typography>
      </Card>
    );
  }

  return (
    <Card
      sx={{
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          height: '4px',
          background: theme =>
            theme.palette.mode === 'light'
              ? 'linear-gradient(90deg, #e3f2fd, #bbdefb, #90caf9)'
              : 'linear-gradient(90deg, #424242, #616161, #424242)',
        },
      }}
    >
      <Box sx={{ p: 3 }}>
        <Typography variant="h6" sx={{ mb: 2 }}>情感分布</Typography>
        <Box sx={{ height: 300, position: 'relative' }}>
          <ResponsivePie
            data={data}
            margin={{ top: 20, right: 20, bottom: 20, left: 20 }}
            innerRadius={0.6}
            padAngle={0.7}
            cornerRadius={3}
            activeOuterRadiusOffset={8}
            colors={({ data }) => `linear-gradient(135deg, ${data.gradient[0]}, ${data.gradient[1]})`}
            borderWidth={1}
            borderColor={{
              from: 'color',
              modifiers: [['darker', 0.2]],
            }}
            enableArcLinkLabels={false}
            arcLabelsSkipAngle={10}
            arcLabelsTextColor="#fff"
            onMouseEnter={(data) => setHoveredId(data.id)}
            onMouseLeave={() => setHoveredId(null)}
            motionConfig={{
              mass: 1,
              tension: 170,
              friction: 26,
            }}
            transitionMode="pushIn"
          />
        </Box>
        <Box
          sx={{
            display: 'flex',
            justifyContent: 'center',
            flexWrap: 'wrap',
            gap: 3,
            mt: 2,
          }}
        >
          {data.map((item) => (
            <Fade key={item.id} in timeout={500}>
              <Box
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: 1,
                  padding: '8px 16px',
                  borderRadius: '12px',
                  transition: 'all 0.3s ease',
                  background: hoveredId === item.id
                    ? `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`
                    : 'transparent',
                  color: hoveredId === item.id ? '#fff' : 'text.primary',
                  cursor: 'pointer',
                  '&:hover': {
                    background: `linear-gradient(135deg, ${item.gradient[0]}, ${item.gradient[1]})`,
                    color: '#fff',
                  },
                }}
                onMouseEnter={() => setHoveredId(item.id)}
                onMouseLeave={() => setHoveredId(null)}
              >
                {item.icon}
                <Typography
                  variant="body2"
                  sx={{
                    fontWeight: 500,
                    minWidth: 100,
                    textAlign: 'center',
                  }}
                >
                  {item.label}: {getPercentage(item.value)}%
                </Typography>
              </Box>
            </Fade>
          ))}
        </Box>
      </Box>
    </Card>
  );
};

export default EmotionChart;
