import { useState, useEffect, useMemo } from 'react';
import { Card, Typography, Box, Fade } from '@mui/material';
import { keyframes } from '@mui/system';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAlt';
import SentimentNeutralIcon from '@mui/icons-material/SentimentNeutral';
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';

// 动画关键帧
const rotateAnimation = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

const pulseAnimation = keyframes`
  0% { transform: scale(1); opacity: 0.5; }
  50% { transform: scale(1.05); opacity: 0.8; }
  100% { transform: scale(1); opacity: 0.5; }
`;

// 配色方案
const colorScheme = {
  positive: {
    primary: '#36BFFA',
    secondary: '#2FD4B6',
    shadow: 'rgba(54, 191, 250, 0.3)',
  },
  neutral: {
    primary: '#818CF8',
    secondary: '#C084FC',
    shadow: 'rgba(129, 140, 248, 0.3)',
  },
  negative: {
    primary: '#FB7185',
    secondary: '#F472B6',
    shadow: 'rgba(251, 113, 133, 0.3)',
  },
};

const EmotionChart = ({ history }) => {
  const [hoveredId, setHoveredId] = useState(null);
  const [animate, setAnimate] = useState(false);

  useEffect(() => {
    setAnimate(true);
    return () => setAnimate(false);
  }, [history]);

  const data = useMemo(() => {
    const sentimentCounts = history.reduce((acc, item) => {
      acc[item.sentiment] = (acc[item.sentiment] || 0) + 1;
      return acc;
    }, {});

    const total = history.length;

    return [
      {
        id: 'positive',
        label: '正面',
        value: sentimentCounts.positive || 0,
        percentage: ((sentimentCounts.positive || 0) / total * 100).toFixed(1),
        colors: colorScheme.positive,
        icon: <SentimentSatisfiedAltIcon />,
      },
      {
        id: 'neutral',
        label: '中性',
        value: sentimentCounts.neutral || 0,
        percentage: ((sentimentCounts.neutral || 0) / total * 100).toFixed(1),
        colors: colorScheme.neutral,
        icon: <SentimentNeutralIcon />,
      },
      {
        id: 'negative',
        label: '负面',
        value: sentimentCounts.negative || 0,
        percentage: ((sentimentCounts.negative || 0) / total * 100).toFixed(1),
        colors: colorScheme.negative,
        icon: <SentimentVeryDissatisfiedIcon />,
      },
    ];
  }, [history]);

  const commonCardStyles = {
    background: 'rgba(17, 25, 40, 0.75)',
    backdropFilter: 'blur(20px) saturate(180%)',
    border: '1px solid rgba(255, 255, 255, 0.125)',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.37)',
  };

  if (history.length === 0) {
    return (
      <Card
        sx={{
          ...commonCardStyles,
          p: 4,
          height: '100%',
          minHeight: 400,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          gap: 3,
        }}
      >
        <Box
          sx={{
            width: 180,
            height: 180,
            position: 'relative',
            '&::before': {
              content: '""',
              position: 'absolute',
              inset: -3,
              background: 'conic-gradient(from 0deg, #36BFFA, #2FD4B6, #818CF8, #36BFFA)',
              borderRadius: '50%',
              animation: `${rotateAnimation} 4s linear infinite`,
              filter: 'blur(8px)',
              opacity: 0.4,
            },
            '&::after': {
              content: '""',
              position: 'absolute',
              inset: 6,
              background: 'rgba(17, 25, 40, 0.95)',
              borderRadius: '50%',
              zIndex: 1,
            },
          }}
        >
          <Box
            sx={{
              position: 'absolute',
              inset: 0,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              zIndex: 2,
              animation: `${pulseAnimation} 3s ease-in-out infinite`,
            }}
          >
            <SentimentNeutralIcon 
              sx={{ 
                fontSize: 64,
                color: '#818CF8',
                filter: 'drop-shadow(0 0 10px rgba(129, 140, 248, 0.5))',
              }} 
            />
          </Box>
        </Box>
        <Typography
          variant="h6"
          sx={{
            background: 'linear-gradient(135deg, #36BFFA, #2FD4B6)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 600,
            textShadow: '0 0 20px rgba(54, 191, 250, 0.3)',
          }}
        >
          暂无分析数据
        </Typography>
      </Card>
    );
  }

  return (
    <Card sx={{ ...commonCardStyles, p: 4, minHeight: 400 }}>
      <Typography
        variant="h6"
        sx={{
          mb: 4,
          textAlign: 'center',
          background: 'linear-gradient(135deg, #36BFFA, #2FD4B6)',
          backgroundClip: 'text',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          fontWeight: 600,
          letterSpacing: '0.5px',
          position: 'relative',
          '&::after': {
            content: '""',
            position: 'absolute',
            bottom: -8,
            left: '50%',
            transform: 'translateX(-50%)',
            width: 40,
            height: 3,
            background: 'linear-gradient(90deg, #36BFFA, #2FD4B6)',
            borderRadius: 4,
          },
        }}
      >
        情感分布
      </Typography>

      {/* 图表部分代码继续... */}
    </Card>
  );
};

export default EmotionChart;
