import { useState, useEffect } from 'react';
import { ThemeProvider, CssBaseline, Box } from '@mui/material';
import { keyframes } from '@mui/system';
import { lightTheme, darkTheme } from './theme';
import TextAnalyzer from './components/TextAnalyzer';
import ThemeToggle from './components/ThemeToggle';
import ParticlesBackground from './components/ParticlesBackground';

// 动画关键帧
const gradientAnimation = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const glowAnimation = keyframes`
  0% { opacity: 0.3; }
  50% { opacity: 0.5; }
  100% { opacity: 0.3; }
`;

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({
        x: (e.clientX / window.innerWidth) * 2 - 1,
        y: (e.clientY / window.innerHeight) * 2 - 1,
      });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <CssBaseline />
      <ParticlesBackground />
      <Box
        sx={{
          minHeight: '100vh',
          position: 'relative',
          overflow: 'hidden',
          background: theme => 
            theme.palette.mode === 'light'
              ? 'linear-gradient(135deg, #E3F2FD, #F5F5F7)'
              : 'linear-gradient(135deg, #0A1929, #1A1A2B)',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: theme =>
              theme.palette.mode === 'light'
                ? `radial-gradient(circle at ${50 + mousePosition.x * 10}% ${50 + mousePosition.y * 10}%, 
                    rgba(124, 207, 255, 0.1) 0%,
                    rgba(77, 208, 225, 0.1) 25%,
                    rgba(54, 191, 250, 0.1) 50%,
                    rgba(47, 212, 182, 0.1) 75%,
                    rgba(255, 255, 255, 0) 100%)`
                : `radial-gradient(circle at ${50 + mousePosition.x * 10}% ${50 + mousePosition.y * 10}%, 
                    rgba(54, 191, 250, 0.15) 0%,
                    rgba(47, 212, 182, 0.15) 25%,
                    rgba(77, 208, 225, 0.1) 50%,
                    rgba(124, 207, 255, 0.05) 75%,
                    rgba(0, 0, 0, 0) 100%)`,
            animation: `${glowAnimation} 4s ease-in-out infinite`,
            pointerEvents: 'none',
          },
          '&::after': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: theme =>
              theme.palette.mode === 'light'
                ? 'linear-gradient(45deg, rgba(255,255,255,0.2) 0%, rgba(255,255,255,0) 100%)'
                : 'linear-gradient(45deg, rgba(0,0,0,0.2) 0%, rgba(0,0,0,0) 100%)',
            backgroundSize: '200% 200%',
            animation: `${gradientAnimation} 15s ease infinite`,
            pointerEvents: 'none',
          },
          padding: { xs: 2, md: 4 },
        }}
      >
        <Box
          sx={{
            position: 'relative',
            zIndex: 1,
          }}
        >
          <ThemeToggle isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
          <TextAnalyzer />
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
